INSERT INTO `hoi_dap` (`ma_hd`, `parent`, `noidung_hd`, `thoigian_hd`, `ma_sp`, `ten_k`, `ma_nv`, `status`, `ghichu_hd`) VALUES
(26, 0, 'Cho tôi hỏi sao tôi mới cập nhật phần mềm lên 5.0 UI,thì đt bị hao pin nhanh hơn lúc chưa cập nhật, sạc 100% để chừng 15 phút k xài là hao 8% pin, nhờ qtrvien nghien cứu dùm ak', '2023-11-16 14:03:17', 29, 'Nguyễn Anh Tú', 4, 0, 'dienthoai'),
(27, 0, 'ss a32 vs a23 chiếc nào mạnh mẽ về hiệu năng chơi game, camera tốt, dùng để lướt web học tập vậy ạ(ý chung là hơn về mọi mặt luôn á nha)', '2023-11-16 14:04:02', 29, 'Nguyễn Anh Tú', 4, 0, 'dienthoai'),
(28, 26, 'Chào chị,\r\nDạ trường hợp này có lẽ do bản cập nhật mới có nhiều tính năng nên máy sẽ hơi hao pin hơn bình thường đó ạ , nếu tình trạng hao pin ngày càng tăng thì anh mang máy đến cửa hàng để nhân viên kiểm tra xem sao nhé\r\nThông tin đến chị.', '2023-11-16 14:04:43', 29, 'Nguyễn Anh Tú', 5, 1, ''),
(29, 0, 'Đồng hồ định vị sai vị trí lên đến 1km . Bảo hành đi đi lại lại vẫn thế', '2023-11-16 17:02:49', 129, 'Nguyễn Anh Tú', 4, 0, 'smartwatch'),
(30, 29, 'Chào chị . Dạ chị vui lòng cho em xin số điện thoại mua hàng để em có thể hỗ trợ mình chị nha. Thông tin đến chị ', '2023-11-15 17:03:16', 129, 'Nguyễn Anh Tú', 5, 1, ''),
(31, 0, 'Cho mình hỏi mua trả góp iphone 14 pro 128gb qua thẻ tín dụng TP bank, trả trước 15tr,còn lại quẹt thẻ tín dụng , thì phí chuyển đổi trả góp dự kiến là bao nhiêu ạ? mình trả góp 12 tháng .', '2023-11-15 14:32:41', 69, 'Nguyễn Anh Tú', 4, 0, 'maytinhdeban'),
(32, 0, 'test cmtvvvvvvvvv', '2023-07-19 15:47:51', 75, 'Nguyễn Anh Tú', 4, 0, 'dienthoai'),
(33, 0, 'Cho mình hỏi nên mua samsung hay iphone', '2023-11-19 15:21:19', 25, 'Tú Anh', 4, 0, 'maytinhdeban');
